+import { ExpandMore as ExpandMoreIcon, KeyboardArrowDown as ArrowDownIcon } from '@mui/icons-material';
+import type { PoolSummary, TokenResponse } from '../../lib/types';
+
+interface Props {
+  detail: TokenResponse;
+  pairId: string;
+  pools: PoolSummary[];
+  chain: string;
+  onPoolSwitch: (p: PoolSummary) => void;
+}
+
