+  color: var(--text);
+  font-weight: 700;
+  font-size: 16px;
+  line-height: 1.2;
+  display: block;
+}
+
+.kpi-item strong.pos, 
+.kpi-item span.pos { 
+  color: var(--accent-lime);
+}
+
+.kpi-item strong.neg, 
+.kpi-item span.neg { 
+  color: var(--accent-maroon);
+}
+
