+  
+  .detail-kpis {
+    padding: 0 12px 20px;
+    gap: 10px;
+  }
+  
+  .kpi-item {
+    padding: 12px;
+  }
+  
+  .kpi-item strong {
+    font-size: 14px;
+  }
+  
+  .detail-addrs,
+  .detail-links,
