+  }
+  
+  .limitation-notice {
+    margin-left: 12px;
+    margin-right: 12px;
+  }
+  
+  .detail-pools {
+    padding: 0 12px 20px;
+  }
+  
+  .addr-row code {
+    font-size: 11px;
+    padding: 6px 8px;
+  }
+  
+  .detail-links a {
+    width: 40px;
+    height: 40px;
+  }
