}

/* Chart Page Specific Styles */
.chart-page {
  background: var(--bg);
  min-height: 100vh;
}

.chart-content {
  background: var(--bg);
}

.limitation-notice {
  background: rgba(var(--accent-maroon-rgb), 0.1);
  border: 1px solid rgba(var(--accent-maroon-rgb), 0.2);
  color: var(--accent-maroon);
  padding: 12px 16px;
  margin: 0 16px 16px;
  border-radius: 10px;
  font-size: 14px;
  font-weight: 500;
  text-align: center;
}

.chart-container,
.trades-container {
  padding: 0 16px;
  margin-bottom: 24px;
}

.chart-container > div,
.trades-container > div {
  border-radius: 16px;
  overflow: hidden;
  background: var(--bg-elev);
  border: 1px solid var(--border-subtle);
}

/* Mobile Optimizations */
@media (max-width: 768px) {
  .detail-top {
    gap: 12px;
    padding: 12px;
  }

  .detail-avatar img,
  .detail-letter {
    width: 56px;
    height: 56px;
    border-radius: 14px;
  }

  .detail-letter {
    font-size: 20px;
  }

  .detail-title strong {
    font-size: 18px;
  }

  .pool-selector {
    max-width: 160px;
    font-size: 12px;
    padding: 5px 10px;
  }

  .detail-kpis {
    padding: 0 12px 20px;
    gap: 10px;
  }

  .kpi-item {
    padding: 12px;
  }

  .kpi-item strong {
    font-size: 14px;
  }

  .detail-addrs,
  .detail-links,
  .chart-container,
  .trades-container {
    margin-left: 12px;
    margin-right: 12px;
  }

  .limitation-notice {
    margin-left: 12px;
    margin-right: 12px;
  }

  .detail-pools {
    padding: 0 12px 20px;
  }

  .addr-row code {
    font-size: 11px;
    padding: 6px 8px;
  }

  .detail-links a {
    width: 40px;
    height: 40px;
  }
}
