+  border-bottom: 1px solid var(--border-subtle);
+  animation: expandRow 0.3s ease-out;
+  border-left: 3px solid var(--border-light);
+}
+
+@keyframes expandRow {
+  from {
+    opacity: 0;
+    transform: translateY(-10px);
+    max-height: 0;
+    padding: 0 16px;
+  }
