+    opacity: 1;
+    transform: translateY(0);
+    max-height: 300px;
+    padding: 16px;
+  }
+}
+
+.tr-expanded-content {
+  display: flex;
+  flex-direction: column;
+  gap: 16px;
+}
+
+.expanded-section {
+  display: flex;
+  flex-direction: column;
+  gap: 8px;
+}
+
+.expanded-section-title {
+  font-size: 12px;
+  font-weight: 600;
+  color: var(--text-muted);
+  text-transform: uppercase;
