+    grid-template-columns: 1fr;
+  }
+}
+
+.stat-item {
+  display: flex;
+  justify-content: space-between;
+  align-items: center;
+  padding: 6px 8px;
+  background: var(--bg-elev);
+  border-radius: var(--radius-small);
+  font-size: 13px;
+}
+
+.stat-label {
+  color: var(--text-muted);
+  font-weight: 500;
+}
+
+.stat-value {
+  color: var(--text);
+  font-weight: 600;
+  font-family: 'SF Mono', Monaco, 'Cascadia Code', monospace;
+}
+
+.buy-count {
+  color: var(--accent-lime);
+}
+
+.sell-count {
+  color: var(--accent-maroon);
+}
+
+.stat-separator {
+  color: var(--text-muted);
+  margin: 0 4px;
+}
+
+/* Maker column no-wrap */
+.maker-column {
+  white-space: nowrap;
+  overflow: hidden;
+}
+
+.maker-addr {
+  font-family: 'SF Mono', Monaco, 'Cascadia Code', monospace;
+  font-size: 13px;
+  color: var(--text);
+  white-space: nowrap;
+}
+
+/* Ensure trades table fills the available space properly */
+.trades-view-content {
+  height: calc(100vh - 60px - 80px - env(safe-area-inset-bottom)); /* Full height minus header, tabs, and safe area */
+  display: flex;
+  flex-direction: column;
+}
+
+.trades-container {
+  flex: 1;
+  display: flex;
+  flex-direction: column;
+  min-height: 0; /* Allow flex shrinking */
+}

